// SPDX-License-Identifier: MIT
// Tells the Solidity compiler to compile only from v0.8.13 to v0.9.0
pragma solidity ^0.8.13;

contract BonfireApp {
    // shows the addresses that the key allows access to
    mapping(address => mapping(address => bool)) allowedWallets;

    // show the addresses that the key is allowed access to
    mapping(address => mapping(address => bool)) userWhitelistedWallets;

    // function getWhitelistForAddress(
    //     address _address
    // ) public view returns (address[] memory) {}

    function checkPermission(
        address _owner,
        address _requester
    ) public view returns (bool) {
        return allowedWallets[_owner][_requester];
    }

    function addAccess(address _requester) public {
        allowedWallets[msg.sender][_requester] = true;
        userWhitelistedWallets[_requester][msg.sender] = true;
    }

    function deleteAccess(address _requester) public {
        allowedWallets[msg.sender][_requester] = false;
        userWhitelistedWallets[_requester][msg.sender] = false;
    }
}
